/** * Definition for a binary tree node. * struct TreeNode { *     int val; *     TreeNode *left; *     TreeNode *right; *     TreeNode(int x) : val(x), left(NULL), right(NULL) {} * }; */class Solution {public:   TreeNode* sortedArrayToBST(vector<int>& nums) {  		TreeNode *res;		 if(nums.size()==0)  			return res;		res=build(nums,0,nums.size()-1);		return res;    }    TreeNode* build(vector<int>& tree,int low,int high)	{		if(low>high)		{			return NULL;		}        int mid = low+(high-low)/2 ;		TreeNode *res=new TreeNode(tree[mid]);		res->left=build(tree,low,mid-1);		res->right=build(tree,mid+1,high);		return res;	}};